import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import api from '../../services/api';

import Container from '../../components/container';
import { Loading, Owner, IssuesList, ListBox } from './styles';

export default class Repository extends Component {
  PropTypes = {
    match: PropTypes.shape({
      params: PropTypes.shape({
        repository: PropTypes.string,
      }),
    }).isRequired,
  };

  state = {
    repository: {},
    issues: [],
    loading: true,
    issuesStates: [],
  };

  async componentDidMount() {
    const { match } = this.props;
    const { issuesState } = this.state;
    const repoName = decodeURIComponent(match.params.repository);

    const [repository, issues] = await Promise.all([
      api.get(`/repos/${repoName}`),
      api.get(`/repos/${repoName}/issues`, {
        params: {
          state: issuesState,
          per_page: 5,
        },
      }),
    ]);

    this.setState({
      repository: repository.data,
      issues: issues.data,
      loading: false,
    });
  }

  handleInput = e => {
    const { issuesStates } = this.state;
    this.setState({ issuesStates: [...issuesStates, e.target.id] });
    if (issuesStates.find(e.taget.id)) {
      this.setState({ issuesState: [issuesStates] });
    }
    console.log(issuesStates);
  };

  render() {
    const { repository, issues, loading, issuesStates } = this.state;
    const arrayIssues = ['open', 'closed'];

    if (loading) {
      return <Loading>Carregando</Loading>;
    }

    return (
      <Container>
        <Owner>
          <Link to="/">Voltar aos repositórios</Link>
          <img src={repository.owner.avatar_url} alt={repository.owner.login} />
          <h1>{repository.name}</h1>
          <p>{repository.description}</p>
        </Owner>
        <ListBox>
          <h2>Listar repositórios:</h2>
          {arrayIssues.map(arrayIssue => (
            <label htmlFor={arrayIssue}>
              <input
                onChange={this.handleInput}
                type="checkbox"
                id={arrayIssue}
              />
              <p key={arrayIssue}>{arrayIssue}</p>
            </label>
          ))}
        </ListBox>
        <IssuesList>
          {issues.map(issue => (
            <li key={String(issue.id)}>
              <img src={issue.user.avatar_url} alt={issue.user.login} />
              <div>
                <strong>
                  <a href={issue.html_url}>{issue.title}</a>
                  {issue.labels.map(label => (
                    <span key={String(label.id)}>{label.name}</span>
                  ))}
                </strong>
                <p>{issue.user.login}</p>
              </div>
            </li>
          ))}
        </IssuesList>
      </Container>
    );
  }
}
